package org.jax.svann.overlap;

import de.charite.compbio.jannovar.data.ReferenceDictionary;
import de.charite.compbio.jannovar.data.ReferenceDictionaryBuilder;
import de.charite.compbio.jannovar.reference.*;

/**
 * Allows the easy creation of transcript models from knownGenes.txt.gz lines.
 * Used here to create TranscriptModel objects for testing
 *
 * @author <a href="mailto:manuel.holtgrewe@charite.de">Manuel Holtgrewe</a>
 * @author <a href="mailto:peter.robinson@jax.org">Peter Robinson</a>
 */
public class TranscriptModelFactory {

    private static ReferenceDictionary rdict;

    static {
        ReferenceDictionaryBuilder rdbuilder = new ReferenceDictionaryBuilder();
        rdbuilder.putContigID("1", 1);
        rdbuilder.putContigName(1, "1");
        int chr1_length = 249250621; // hg37, from http://hgdownload.soe.ucsc.edu/goldenPath/hg19/database/chromInfo.txt.gz
        rdbuilder.putContigLength(1, chr1_length);
        rdict = rdbuilder.build();
    }

    /**
     * Helper function to parse a knownGenes.txt.gz line into a TranscriptModel.
     *
     * @param refDict //	 *            reference dictionary
     * @param s       The knownGeneList line to parse.
     */
    public static TranscriptModelBuilder parseKnownGenesLine(ReferenceDictionary refDict, String s) {
        String[] fields = s.split("\t");
        TranscriptModelBuilder result = new TranscriptModelBuilder();
        result.setAccession(fields[0]);

        int chr = refDict.getContigNameToID().get(fields[1].substring(3));

        result.setStrand(fields[2].charAt(0) == '+' ? Strand.FWD : Strand.REV);
        GenomeInterval txRegion = new GenomeInterval(refDict, Strand.FWD, chr, Integer.parseInt(fields[3]) + 1,
                Integer.parseInt(fields[4]), PositionType.ONE_BASED);
        result.setTXRegion(txRegion);
        GenomeInterval cdsRegion = new GenomeInterval(refDict, Strand.FWD, chr, Integer.parseInt(fields[5]) + 1,
                Integer.parseInt(fields[6]), PositionType.ONE_BASED);
        result.setCDSRegion(cdsRegion);

        int exonCount = Integer.parseInt(fields[7]);
        String[] startFields = fields[8].split(",");
        String[] endFields = fields[9].split(",");
        for (int i = 0; i < exonCount; ++i) {
            GenomeInterval exonRegion = new GenomeInterval(refDict, Strand.FWD, chr,
                    Integer.parseInt(startFields[i]) + 1, Integer.parseInt(endFields[i]), PositionType.ONE_BASED);
            result.addExonRegion(exonRegion);
        }

        return result;
    }

    /**
     * Create a model for this forward-strand gene on chromosome one with 11 exons
     * 6640062,6640600,6642117,6645978,6646754,6647264,6647537,6648119,6648337,6648815,6648975,
     * 6640196,6641359,6642359,6646090,6646847,6647351,6647692,6648256,6648502,6648904,6649340,
     * Intron 1 is between 6640196-6640600
     * Intron 2 is between 6641359-6642117
     * Intron 3 is between 6642359-6645978
     * Intron 4 is between 6646090-6646754
     * Intron 10 is between 6648815-6649340
     * @return a transcript model representing ZBTB48
     */
    public static TranscriptModel ZBTB48() {
        TranscriptModelBuilder builderForward = TranscriptModelFactory
                .parseKnownGenesLine(
                        rdict,
                        "uc001anx.3	chr1	+	6640062	6649340	6640669	6649272	11	6640062,6640600,6642117,6645978,6646754,6647264,6647537,6648119,6648337,6648815,6648975,	6640196,6641359,6642359,6646090,6646847,6647351,6647692,6648256,6648502,6648904,6649340,	P10074	uc001anx.3");
        builderForward
                .setSequence(""
                        .toUpperCase());
        builderForward.setGeneSymbol("ZBTB48");
        return builderForward.build();
    }

    /**
     * ZNF436 is a gene with 4 exons on the reverse strand of chromosome 1
     * 23685940,23693534,23694465,23695858,
     * 23689714,23693661,23694558,23696357,
     * Intron 1 is 23694558-23695858
     * Intron 2 is 23693661- 23694465
     * Intron 3 is23689714-23693534
     * @return a transcript model representing ZNF436
     */
    public static TranscriptModel ZNF436() {
        TranscriptModelBuilder builderReverse = TranscriptModelFactory
                .parseKnownGenesLine(
                        rdict,
                        "uc001bgu.3	chr1	-	23685940	23696357	23688461	23694498	4	23685940,23693534,23694465,23695858,	23689714,23693661,23694558,23696357,	Q9C0F3	uc001bgu.3");
        builderReverse
                .setSequence(""
                        .toUpperCase());
        builderReverse.setGeneSymbol("ZNF436");
        return builderReverse.build();
    }
    /*

     */

    public static ReferenceDictionary rdict() {
        return rdict;
    }

}

